import React from "react";
import { useState } from "react";

const Main = () => {
  const [state, setState] = useState([
    {
      name: "Ghadier Mutar",
      img:"https://avatars.githubusercontent.com/u/92247998?v=4",
      current: "Junior JS Developer",
      location:"Buraidah,Saudi Arabia",
      email: "Ghadier.Mutar.elq.js@tuwaiq.edu.sa",
      about:
        "For the past 4 years i've been interested in programming and computer science so I sign up with 1 Million Arab Coders in 2017 but my computer was so weak so it crashed from the first month, I decided to start learning this year with tuwaiq bootcamp and here I'm coding everyday.",
      experience: `I am currently a Trainee as a developer at Tuwaiq bootcamp, I have had
  experience in HTML, CSS, JavaScript and how to use Github for saving my
  progress in the last few weeks. Now I am learning React and how to write
  a clean simple code in it.`,
      skills: ["HTML", "CSS", "ES6", "Github"],
      education: [
        {
          field: "Bachelor Special Education",
          university: "Majmaah University",
          startDate: "09/2012",
          endDate: "02/2017",
        },
      ],
    },
  ]);

  return (
    <div className="mainProf">
      <div className="cvImg">{state.img}</div>
      <h1>{state.name}</h1>
      <h2>{state.current} </h2>
      <hr></hr>
      <p>{state.location}</p>
      <p>{state.email}</p> <hr></hr>
      <img src="https://img.icons8.com/windows/32/000000/twitter.png" />
      <img src="https://img.icons8.com/ios-glyphs/30/000000/github.png" />
      <hr></hr>
      <p>
        I built this site with React components and a JSON Resome Schema. The
        full source code can be found in my Github repo.
      </p>
    </div>
  );
};
export default Main;
